import React, { Component } from 'react'
import ShopCustomerService from '../service/ShopCustomerService';

const SHOPID = 1

class ListShopCustomersComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            shopCustomers: [],
            message: null
        }
        this.deleteCustomerClicked = this.deleteCustomerClicked.bind(this)
        this.updateCustomerClicked = this.updateCustomerClicked.bind(this)
        this.addCustomerClicked = this.addCustomerClicked.bind(this)
        this.refreshCustomers = this.refreshCustomers.bind(this)
    }

    componentDidMount() {
        this.refreshCustomers();
    }

    refreshCustomers() {
        ShopCustomerService.retrieveAllCustomers(SHOPID)//HARDCODED
            .then(
                response => {
                    //console.log(response);
                    this.setState({ shopCustomers: response.data })
                }
            )
    }

    deleteCustomerClicked(customerId) {
        ShopCustomerService.deleteCustomer(SHOPID, customerId)
            .then(
                response => {
                    this.setState({ message: `Delete of customer id ${customerId} Successful` })
                    this.refreshCustomers()
                }
            )

    }

    addCustomerClicked() {
        this.props.history.push(`/shopCustomers/-1`)
    }

    updateCustomerClicked(id) {
        console.log('update ' + id)
        this.props.history.push(`/shopCustomers/${id}`)
    }

    render() {
        console.log('render')
        return (
            <div className="container">
                <h3>All Shop Customers</h3>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>CustomerId</th>
                                <th>Balance</th>
                                <th>UpdateBalance</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.shopCustomers.map(
                                    shopCustomer =>
                                        <tr key={shopCustomer.customerId}>
                                            <td>{shopCustomer.customerId}</td>
                                            <td>{shopCustomer.balance}</td>
                                            <td><button className="btn btn-success" onClick={() => this.updateCustomerClicked(shopCustomer.customerId)}>Update</button></td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteCustomerClicked(shopCustomer.customerId)}>Delete</button></td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addCustomerClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default ListShopCustomersComponent
